import React from 'react';
import Button from '@material-ui/core/Button';
import Dialog from '@material-ui/core/Dialog';
import DialogActions from '@material-ui/core/DialogActions';
import DialogContent from '@material-ui/core/DialogContent';
import DialogTitle from '@material-ui/core/DialogTitle';
import Typography from '@material-ui/core/Typography';
import TextField from '@material-ui/core/TextField';
import Grid from '@material-ui/core/Grid';
import fhirdata from 'src/__mocks__/fhirdata';
import { FhirClientContext } from 'src/FhirClientContext';

export default class VitalUpdate extends React.Component {
    static contextType = FhirClientContext;

    constructor(props) {
      super(props);
      this.state = {
        loading: true,
        patient: null,
        error: null,
        client: null,
        open: false,
        rr: '',

      };
    }

    componentDidMount() {
      this.setState({ client: this.context.client });
      console.log(this.context.client);
    }

    render() {
      const handleClose = () => {
        const vitaltypes = ['rr'];
        this.setState({ open: false });
        const date = new Date();
        const isodate = date.toISOString();
        const pid = this.context.client.patient.id;

        Object.values(vitaltypes).forEach((type) => {
          if (this.state[type]) {
            const data = fhirdata[type];
            data.valueQuantity.value = this.state[type];
            data.effectiveDateTime = date;
            data.issued = isodate;
            data.subject.reference = 'Patient/' + pid;
            this.context.client.create(data);
          }
        });

        this.setState({
          rr: '',
        });
        this.props.getVitals();
      };

      const inputChangeHandler = (event) => {
        this.setState({
          [event.target.name]: event.target.value
        });
      };

      return (
        <div>
          <Button variant="outlined" color="primary" onClick={() => this.setState({ open: true })}>
            Update Weight Annotation
          </Button>
          <Dialog open={this.state.open} aria-labelledby="form-dialog-title">
            <DialogContent>
              <Typography variant="h6" gutterBottom>
              Weight Annotation
              </Typography>
              <Grid item xs={12}>
                <TextField
                  id="weight"
                  name="weight"
                  label="Annotation"
                  onChange={inputChangeHandler}
                  fullWidth
                />
              </Grid>
            </DialogContent>
            <DialogActions>
              <Button onClick={handleClose} color="primary">
                Cancel
              </Button>
              <Button onClick={handleClose} color="primary">
                Sumbit Vitals
              </Button>
            </DialogActions>
          </Dialog>
        </div>
      );
    }
}
