import { useState } from 'react';
import PropTypes from 'prop-types';
import moment from 'moment';
import PerfectScrollbar from 'react-perfect-scrollbar';
import {
  Box,
  Card,
  Table,
  TableBody,
  TableCell,
  TableHead,
  TablePagination,
  TableRow,
  Typography
} from '@material-ui/core';

const CustomerListResults = ({ medications, ...rest }) => {
  const [selectedCustomerIds] = useState([]);
  const [limit, setLimit] = useState(10);
  const [page, setPage] = useState(0);

  const handleLimitChange = (event) => {
    setLimit(event.target.value);
  };

  const handlePageChange = (event, newPage) => {
    setPage(newPage);
  };

  return (
    <Card {...rest}>
      <PerfectScrollbar>
        <Box sx={{ minWidth: 1050 }}>
          <Table>
            <TableHead>
              <TableRow>
                <TableCell>
                  Medication
                </TableCell>
                <TableCell>
                  Dosage Instructions
                </TableCell>
                <TableCell>
                  Duration
                </TableCell>
                <TableCell>
                  Date Prescribed
                </TableCell>
                <TableCell>
                  Notes
                </TableCell>
                <TableCell>
                  Reason for Medication
                </TableCell>
              </TableRow>
            </TableHead>
            <TableBody>
              {medications.slice(0, limit).map((medication) => (
                <TableRow
                  hover
                  key={medication.id}
                  selected={selectedCustomerIds.indexOf(medication.id) !== -1}
                >
                  <TableCell>
                    <Box
                      sx={{
                        alignItems: 'center',
                        display: 'flex'
                      }}
                    >
                      <Typography
                        color="textPrimary"
                        variant="body1"
                      >
                        {medication.medicationCodeableConcept.text}
                      </Typography>
                    </Box>
                  </TableCell>
                  <TableCell>
                    {meidcation.intent}
                  </TableCell>
                  <TableCell>
                    test
                  </TableCell>
                  <TableCell>
                    {medication.status}
                  </TableCell>
                  <TableCell>
                    {moment(medication.authoredOn).format('DD/MM/YYYY')}
                  </TableCell>
                </TableRow>
              ))}
            </TableBody>
          </Table>
        </Box>
      </PerfectScrollbar>
      <TablePagination
        component="div"
        count={medications.length}
        onPageChange={handlePageChange}
        onRowsPerPageChange={handleLimitChange}
        page={page}
        rowsPerPage={limit}
        rowsPerPageOptions={[5, 10, 25]}
      />
    </Card>
  );
};

CustomerListResults.propTypes = {
  medications: PropTypes.array.isRequired
};

export default CustomerListResults;
