import React from 'react';
import { Helmet } from 'react-helmet';
import {
  Box,
  Container,
  Grid
} from '@material-ui/core';
import ProductCard from 'src/components/product//ProductCard';
import products from 'src/__mocks__/products';
import { FhirClientContext } from '../FhirClientContext';

const Products = (teams) => (
  <>
    <Helmet>
      <title>Products | Material Kit</title>
    </Helmet>
    <Box
      sx={{
        backgroundColor: 'background.default',
        minHeight: '100%',
        py: 3
      }}
    >
      <Container maxWidth={false}>
        <Box sx={{ pt: 3 }}>
          <Grid
            container
            spacing={3}
          >
            {teams.map((person) => (
              <Grid
                item
                key={person.id}
                lg={4}
                md={6}
                xs={12}
              >
                <ProductCard person={person} />
              </Grid>
            ))}
          </Grid>
        </Box>
      </Container>
    </Box>
  </>
);

export default class ProductList extends React.Component {
    static contextType = FhirClientContext;

    constructor(props) {
      super(props);
      this.state = {
        loading: true,
        patient: null,
        error: null
      };
    }
    // this loader is used for

    componentDidMount() {
      const client = this.context.client;
      const queryTeam = new URLSearchParams();
      queryTeam.set('patient', client.patient.id);
      console.log(client);
      this._loader = client.request('CareTeam?' + queryTeam, {
        pageLimit: 0, // get all pages
        flat: true // return flat array of Observation resources
      }).then(team => {
        this.setState({ team, loading: false, error: null });
      })
        .catch(error => {
          this.setState({ error, loading: false });
        });
    }

    render() {
      const { error, loading, team } = this.state;
      console.log(team);

      if (loading) {
        return null;
      }
      if (error) {
        console.log(error.message);
        return error.message;
      }
      const teams = Object.values(team);
      return <Products {...teams} />;
    }
}
