export default class Dashboard extends React.Component {
    static contextType = FhirClientContext;

    constructor(props) {
      super(props);
      console.log(props.name);
      console.log('test');
      this.state = {
        loading: true,
        vitals: null,
        error: null,
        name: null
      };
      this.changeVital = this.changeVital.bind(this);
    }
    // this loader is used for

    componentDidMount() {
      const query = new URLSearchParams();
      const client = this.context.client;
      query.set('patient', client.patient.id);
      query.set('_count', 100);
      query.set('_sort', '-date'); // Try this to fetch fewer pages
      query.set('code', [
        'http://loinc.org|8462-4', // diastolic blood pressure
        'http://loinc.org|8480-6', // systolic blood pressure
        'http://loinc.org|55284-4', // blood pressure systolic and diasoltic
        'http://loinc.org|8331-1', // oraltemprature
        'http://loinc.org|59408-5', // SPo2
        'http://loinc.org|8867-4', // heart rate
      ].join(','));
      this._loader = client.request('Observation?' + query, {
        pageLimit: 0, // get all pages
        flat: true // return flat array of Observation resources
      })
        .then(vitals => {
          const byCodes = client.byCodes(vitals, 'code');
          const currentSpo = byCodes('59408-5');
          const currentHeartrate = byCodes('8867-4');
          const currentTemprature = byCodes('8331-1');
          const currentBloodpressure = byCodes('55284-4');
          const name = 'SPO2';

          this.setState({
            vitals, currentSpo, currentHeartrate, currentTemprature, currentBloodpressure, name, loading: false, error: null
          });
        })
        .catch(error => {
          this.setState({ error, loading: false });
        });

      client.create(fhirdata[0]);
    }

    changeVital(vitaltype) {
      alert(vitaltype);
      this.setState({ name: vitaltype });
    }

    render() {
      const {
        error, loading, vitals
      } = this.state;
      if (loading) {
        return null;
      }
      if (error) {
        console.log(error.message);
        return error.message;
      }
      console.log(vitals);
      return (
        <>
          <Helmet>
            <title>Dashboard | Material Kit</title>
          </Helmet>
          <Box
            sx={{
              backgroundColor: 'background.default',
              minHeight: '100%',
              py: 3
            }}
          >
            <Container maxWidth={false}>
              <Grid
                container
                spacing={3}
              >
                <Grid
                  item
                  lg={12}
                  sm={24}
                  xl={12}
                  xs={48}
                >
                  <Covid />
                </Grid>
                <Grid
                  item
                  lg={3}
                  sm={6}
                  xl={3}
                  xs={12}
                >
                   <button type="button" value="hello!" onClick={() => this.changeVital('SPO2')}>
                  <Vitalinfo name="BP" vital={this.state.currentSpo[0]} />
                   </button>
                </Grid>
                <Grid
                  item
                  lg={3}
                  sm={6}
                  xl={3}
                  xs={12}
                >
                  <button type="button" value="hello!" onClick={() => changeVital('Heart Rate')}>
                  <Vitalinfo name="HR" vital={this.state.currentHeartrate[0]} />
                  </button>
                </Grid>
                <Grid
                  item
                  lg={3}
                  sm={6}
                  xl={3}
                  xs={12}
                >
                  <button type="button" value="hello!" onClick={() => changeVital('RR')}>
                  <Vitalinfo name="SPO2" vital={this.state.currentSpo[0]} />
                  </button>
                </Grid>
                <Grid
                  item
                  lg={3}
                  sm={6}
                  xl={3}
                  xs={12}
                >
                  <button type="button" value="hello!" onClick={() => changeVital('Temp')}>
                  <Vitalinfo name="Temprature" vital={this.state.currentSpo[0]} />
                  </button>
                </Grid>
                <Grid
                  item
                  lg={12}
                  md={16}
                  xl={12}
                  xs={16}
                >
                  <VitalForm />
                </Grid>
                <Grid
                  item
                  lg={12}
                  md={16}
                  xl={12}
                  xs={16}
                >
                  <Sales />
                </Grid>
                <Grid
                  item
                  lg={12}
                  md={16}
                  xl={12}
                  xs={16}
                >
                  <VitalsTable name={this.state.name} />
                </Grid>
              </Grid>
            </Container>
          </Box>
        </>
      );
    }
}
