import React from 'react';
import { FhirClientContext } from '../FhirClientContext';

function PatientName({ name = [{ name: 'test' }] }) {
  const styles = {
    span: {
      marginLeft: '2em'
    }
  };

  const entry = name.name.find(nameRecord => nameRecord.use === 'official') || name[0];
  if (!entry) {
    return <h1>No Name</h1>;
  }
  return

  (<div>
    <h1>Name: {entry.given.join(" ") + " " + entry.family}
     <span style={styles.span}>
        Gender: {" "+name.gender}
     </span>

     <span style={styles.span}>
        DOB:{name.birthDate}
     </span>

     </h1>
     </div>)
}

function PatientBanner(patient) {
  console.log(patient);
    return (

        <div>
            <PatientName name={patient} />

        </div>
    );
}

export default class Patient extends React.Component {
    static contextType = FhirClientContext;
    constructor(props) {
        super(props);
        this.state = {
            loading: true,
            patient: null,
            error: null
        };
    }
    // this loader is used for
    componentDidMount() {
        const client = this.context.client;
        console.log(client.patient);
        this._loader = client.patient
            .read()
            .then(patient => {
                this.setState({ patient, loading: false, error: null });
            })
            .catch(error => {
                this.setState({ error, loading: false });
            });
    }
    render() {
        const { error, loading, patient } = this.state;

        if (loading) {
            return null;
        }
        if (error) {
            return error.message;
        }
        return <PatientBanner {...patient} />;
    }
}
